import React, { useState } from 'react';
import styled from 'styled-components';
import InteractionPair from './InteractionPair/InteractionPair';

const Container = styled.div`
  margin-top: 10px;
  max-width: 425px;
  justify-content: space-between;
`;

const TweetInteractions = ({ replies, retweets, likes, likedByUser, retweetedByUser }) => {
  // const [likes, updateLikes] = useState(likes);
  return (
    <Container>
      <InteractionPair type='reply' amount={replies} />
      <InteractionPair type='retweet' amount={retweets} isBold={retweetedByUser} />
      <InteractionPair type='like' amount={likes} isBold={likedByUser} />
    </Container>
  )
}

export default TweetInteractions;
