import React from 'react';
import styled from 'styled-components';
import { getIconData } from '../../../../../InteractionIcon/InteractionIcon';

const Container = styled.div`
  display: flex;
  justify-content: flex-start;
  color: ${props => props.color};
`;

const Circle = styled.div`
  width: 35px;
  height: 35px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 50%;
  background-color: ${props => props.backgroundColor};
`;

const InteractionCountPair = ({ type, amount, isBold }) => {
  const { Icon, color, mutedColor } = getIconData(type);

  return (
    <Container color={color}>
      <Circle backgroundColor={mutedColor}>
        <Icon color={color} size={'15px'} strokeWidth={isBold ? '1px' : '2px'} />
      </Circle>
      <div>{amount}</div>
    </Container>
  );
};

export default InteractionCountPair;
