import React from 'react';
import styled from 'styled-components';
import { getIconData } from '../../../../../InteractionIcon/InteractionIcon';

const Container = styled.div`
  display: flex;
  justify-content: flex-start;
  color: ${(props) => props.color};
`;

const CircleContainer = styled.div`
  position: relative;
`;

const Circle = styled.div`
  width: 35px;
  height: 35px;
  position: absolute;
  top: 1px;
  left: 1px;
  border-radius: 50%;
  background-color: ${(props) => props.backgroundColor};
`;

const Amount = styled.span`
  padding: 0 10px;
`;

const InteractionCountPair = ({ type, amount, isSelected }) => {
  const { Icon, color, mutedColor, fill } = getIconData(type);

  return (
    <Container color={color}>
      <CircleContainer>
        <Circle backgroundColor={mutedColor}>
          <Icon
            color={color}
            size={'15px'}
            strokeWidth={isSelected ? '3px' : '2px'}
            {...(isSelected && fill && { fill: color })}
          />
        </Circle>
      </CircleContainer>
      <Amount>{amount}</Amount>
    </Container>
  );
};

export default InteractionCountPair;
