        color: 'var(--c-light-retweet-green)',
        mutedColor: 'var(--c-light-retweet-green-muted)',
      };
    case 'reply':
      return {
        // Icon: <MessageCircle />,
        Icon: MessageCircle,
        color: 'var(--c-light-reply-blue)',
        mutedColor: 'var(--c-light-reply-blue-muted)',
      };
    default:
      return null;
  }
};


const IconElement = ({ Icon, props }) => <Icon {...props} />;

const StyledIcon = styled(IconElement)`
  color: ${props => props.color};
  background-color: ${props => props.mutedColor};

  &:hover {
    color: var(--c-light-like-red);
  }
`;

export default StyledIcon;





