import React from 'react';
import styled from 'styled-components';
import InteractionIcon, { getIconData } from '../../../../../InteractionIcon/InteractionIcon';

/**
 * TODO:
 * rename component to TweetInteraction
 */
const Container = styled.div`
  display: flex;
  justify-content: flex-start;
  color: ${props => props.color};
`;

const Circle = styled.div`
  border-radius: 50%;
  background-color: ${props => props.backgroundColor};
`;

const InteractionPair = ({ type, amount, isBold }) => {
  const { Icon, color, mutedColor } = getIconData(type);

  return (
    <Container color={color}>
      <Circle backgroundColor={mutedColor}>
        <Icon color={color} size={'15px'} strokeWidth={isBold ? '1px' : '2px'} />
      </Circle>
      <div>{amount}</div>
    </Container>
  );
};

export default InteractionPair;
