import React, { Component } from 'react';
import styled from 'styled-components';
import TweetBody from './TweetBody/TweetBody';
import ExternalInteraction from './ExternalInteraction/ExternalInteraction';

/**
  - Standard tweet
    - User icon - left component
    - UN, handle, timestamp (hrs if under 24, otherwise date (18h, Dec 3))
    - (If reply, include Replying to… - see Reply below in variations)
    - tweet body content
    - Replies, retweets, likes
 */

const Container = styled.div`
  border-bottom: 1px solid ${({ theme }) => theme.borderPrimary};
  color: ${({ theme }) => theme.textPrimary};

  padding: 0 15px;
  padding-top: 15px;
  /* div:first-child {
  } */

  &:hover {
    background-color: ${({ theme }) => theme.primaryDk};
  }
`;

const tweet = {
  // optional field
  externalInteraction: {
    type: 'like', // like || retweet || reply
    users: ['faux twit'], // [] of names, handle
  },
  userInfo: {
    name: 'James',
    handle: '@CaucasianJames',
    avi: 'https://twirpz.files.wordpress.com/2015/06/twitter-avi-gender-balanced-figure.png',
  },
  tweetInfo: {
    time: Date.now(),
    text: 'hello, i am a tweet text',
    media: 'something.jpg',
  },
  tweetInteractions: {
    replies: 1,
    retweets: 5,
    likes: 20,
    likedByUser: true,
    retweetedByUser: false,
  },
};

class Tweet extends Component {
  render() {
    const { externalInteraction } = tweet;
    return (
      <Container>
        {externalInteraction && (
          <ExternalInteraction externalInteraction={externalInteraction} />
        )}
        <TweetBody tweet={tweet} />
      </Container>
    );
  }
}

export default Tweet;
